function initTitleAnim(){jQuery(".page-title .letters").each(function(){jQuery(this).html(jQuery(this).text().replace(/([^\x00-\x80]|\w)/g,"<span class='letter'>$&</span>"))}),anime.timeline({loop:!1}).add({targets:".page-title .letter",rotateY:[-90,0],opacity:1,duration:1750,delay:function(t,e){return 45*e}})}function initProfileAnim(){jQuery(".profile-wrap .headshot img").css("transform","scale(1)")}
